mapper.select:
SELECT count(ireview) AS cnt
FROM t_product_review
WHERE iuser = ${iuser}

SELECT COUNT(iquestion) AS cnt
FROM t_product_question
WHERE iuser = ${iuser}

SELECT CEIL(COUNT(iorder) / ${recordCount}) AS result
FROM t_order
WHERE iuser = ${iuser}

SELECT ceil(count(iquestion) / ${recordCount}) AS result
FROM t_product_question
WHERE iuser = ${iuser}

SELECT point
FROM t_user
WHERE iuser = ${iuser}
