mapper.select:
SELECT iprogram
FROM t_program_question_mapping
WHERE iuser = #{iuser}

SELECT 1 AS result
FROM t_fav_mapping
WHERE iproduct = #{iproduct}
AND iuser = #{iuser}

SELECT 1 AS result, rating AS resultFloat
FROM t_product_review
WHERE iproduct = #{iproduct}
AND iuser = #{iuser}

mapper.delete:
DELETE FROM t_fav_mapping
WHERE iuser = #{iuser}
AND iproduct = #{iproduct}
